import React from 'react';
import './App.css';

class Square extends React.Component {
    render() {
        return (
            <button className="Square" onClick={this.props.onClick}>
                {this.props.value}
            </button>
        );
    }
}

class Board extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(null),
        };
    }

    renderSquare(i) {
        return <Square value={this.props.squares[i]} onClick={()=>this.props.onClick(i)}/>;
    }

    render(){
        return (
            <div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(null),
        };
    }

    squareClick(i) {
        let squares = this.state.squares.slice();
        squares[i] = 'X';
        this.setState({squares});
    }

    render() {
        return (
            <div>
                <div className="game-info"> Now it's your turn: X </div>
                <div className="game-board"> 
                    <Board squares={this.state.squares} onClick={this.squareClick}/>
                </div>
            </div>
        );
    }
}

export default Game;
