import React from 'react';
import './App.css';

class Square extends React.Component {
    render() {
        return (
            <button className="Square" onclick={this.props.onclick}>
                {this.state.value}
            </button>
        );
    }
}

class Board extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(null),
        };
    }

    renderSquare(i) {
        return <Square value={this.state.squares[i]} onclick={i=>this.props.onclick(i)}/>;
    }

    render(){
        return (
            <div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}

class Game extends React.Component {
    constructor(props) {
        super(props);
    }

    squareClick(i) {
        let squares = this.states.squares.slice();
        squares[i] = 'X';
        this.setState({squares});
    }

    render() {
        return (
            <div>
                <div className="game-info"> Now it's your turn: X </div>
                <div className="game-board"> 
                    <Board onclick={this.squareClick}/>
                </div>
            </div>
        );
    }
}

export default Game;
