import React from 'react';
import './App.css';

class Square extends React.Component {
    render() {
        return (
            <button className="Square" onClick={this.props.onClick}>
                {this.props.value}
            </button>
        );
    }
}

class Board extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(null),
        };
    }

    renderSquare(i) {
        return <Square value={this.props.squares[i]} onClick={()=>this.props.onClick(i)}/>;
    }

    render(){
        return (
            <div>
                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="board-row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}

class Game extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            xIsNext: true,
            squares: Array(9).fill(null),
        };
    }

    handleClick(i) {
        if(this.wonGame()) {
            return;
        }
        let squares = this.state.squares.slice();
        let xIsNext = this.state.xIsNext;
        squares[i] = xIsNext ? 'X' : 'O';
        this.setState({squares, xIsNext: !xIsNext});
    }

    wonGame() {
        const Rows = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6]
        ];
        const squares = this.state.squares;
        const sameSquares = Rows.find(([a,b,c])=>squares[a] && squares[a]===squares[b] && squares[a]===squares[c]);
        if(!sameSquares) return null;
        return squares[sameSquares[0]];
    }

    render() {
        const winner = this.wonGame();
        let status = winner ?  `${winner} win!!!  `:  `Now it's your turn: ${this.state.xIsNext ? 'X' : 'O'} `;
        return (
            <div className='game'>
                <div className="game-board"> 
                    <Board squares={this.state.squares} onClick={this.handleClick.bind(this)}/>
                </div>
                <div className="game-info"> {status} </div>
            </div>
        );
    }
}

export default Game;
